import { useRef, useContext } from 'react';
import { useHistory } from 'react-router-dom';

import AuthContext from '../../store/auth-context';
import classes from './ProfileForm.module.css';
import { Form, Button, Container,Row,Col} from 'react-bootstrap';

const ProfileForm = () => {
  const history = useHistory();
const newPasswordInputRef = useRef();
const authCtx = useContext(AuthContext);

const submitHandler = (event) => {
event.preventDefault();

const enteredNewPassword = newPasswordInputRef.current.value;

// add validation

fetch('https://identitytoolkit.googleapis.com/v1/accounts:update?key=AIzaSyBOY-Y4RykiUKME-IVBLrDGGcel9VUpch8', {
method: 'POST',
body: JSON.stringify({
idToken: authCtx.token,
password: enteredNewPassword,
returnSecureToken: false
}),
headers: {
'Content-Type': 'application/json'
}
}).then(res => {
// assumption: Always succeeds!
history.replace('/');
});
};

return (
  <>
  <Container className='border-top text-info bg-success  border-primary'>
  <Row className="justify-content-center -2">

    <Col className='px-3' >Variable width content</Col>

  </Row>

</Container>
<Form className={classes.form} onSubmit={submitHandler}>
<Form.Group className="mb-3" controlId="formBasicPassword">
    <Form.Label>New Password</Form.Label>
    <Form.Control type="password" id='new-password'placeholder="Password" minLength="7" ref={newPasswordInputRef}  />

  </Form.Group>
  <Button variant="primary" type="submit">Submit
  </Button>

</Form>
</>
);
};

export default ProfileForm;
