import { useContext } from 'react';
import { Link } from 'react-router-dom';

import AuthContext from '../../store/auth-context';
import classes from './MainNavigation.module.css';

const MainNavigation = () => {
  const authCtx = useContext(AuthContext);

  const isLoggedIn = authCtx.isLoggedIn;

  const logoutHandler = () => {
    authCtx.logout();
    // optional: redirect the user
  };

  return (
    <header className={classes.header}>


      <nav className="navbar navbar-inverse">

      <div className="container-fluid">
      <div className="navbar-header">
      <button type="button" className="navbar-toggle" data-toggle="collapse" data-target="#myNavbar">
        <span className="icon-bar"></span>
         <span className="icon-bar"></span>                      
      </button>
      <Link to='/'>
        <div class="navbar-brand">React Auth</div>
      </Link>

    </div>

    <div class="collapse navbar-collapse" id="myNavbar">
    <ul class="nav navbar-nav">
        <li class="active"><a href="#">Home</a></li>
        <li class="dropdown">
          <a class="dropdown-toggle" data-toggle="dropdown" href="#">Page 1 <span class="caret"></span></a>
          <ul class="dropdown-menu">
            <li><a href="#">Page 1-1</a></li>
            <li><a href="#">Page 1-2</a></li>
            <li><a href="#">Page 1-3</a></li>
          </ul>
        </li>
        <li><a href="#">Page 2</a></li>
        <li><a href="#">Page 3</a></li>
      </ul>

      <ul class="nav navbar-nav navbar-right">

          {!isLoggedIn && (
            <li>
              <Link to='/auth'><span class="glyphicon glyphicon-log-in"></span>Login</Link>
            </li>
          )}
          {isLoggedIn && (
            <li>
              <Link to='/profile'><span class="glyphicon glyphicon-log-in"></span>Profile</Link>
            </li>
          )}
          {isLoggedIn && (
            <li>
              <button onClick={logoutHandler}><span class="glyphicon glyphicon-log-in"></span>Logout</button>
            </li>
          )}
        </ul>

        </div>
        </div>
      </nav>
    </header>
  );
};

export default MainNavigation;
